import { FC } from 'react';
import { styled } from '@stitches/react';
import { AiFillFolderAdd } from 'react-icons/ai';

// 全体の背景色
const StyledBackground = styled('body', {
  minHeight : '100vh',
  Width : 'auto',
  display : 'block',
  background : 'rgba(39, 100, 119, 0.7)',
  borderTopLeftRadius : '6px',
  borderBottomLeftRadius : '6px',
});


// Topのデザイン
const StyledH1 = styled('h1', {
  fontSize : '2rem',
  marginLeft : '1rem',
})
// サイドバーボタンのデザイン
const StyledSideberButton = styled('button', {
  border : 'none',
  fontSize : '32px',
  marginLeft : '5%',
  with : '100px',
  wordWrap : 'break-word',
})

const optionClick = () => {
  alert('click');
}

// テスト表示配列
const ProOption  = [ 'React', 'TypeScript', 'Node', 'Go', 'Swift' ];
const DesignOption = [ 'ノンデザイナーズ・デザインブック', 'In Design 基礎入門' ];
const BookOption = [ 'リーダブルコード', 'React 実践の教科書', 'Flutter 入門' ];

export const Sideber : FC = () => {
  return (
    <StyledBackground>
        <StyledH1>All Note <AiFillFolderAdd /></StyledH1>
            {ProOption.map((item) => {
              return <StyledSideberButton onClick={optionClick}>{item}</StyledSideberButton>
            })}

            {DesignOption.map((item) => {
              return <StyledSideberButton onClick={optionClick}>{item}</StyledSideberButton>
            })}

            {BookOption.map((item) => {
              return <StyledSideberButton onClick={optionClick}>{item}</StyledSideberButton>
            })}
    </StyledBackground>
    )
  };
